--paste into keyboard shortcuts (Tools>Options>Environment>Keyboard>Query Shortcuts)
--open a new query window, highlight characters that you want to search for, press the shortcut assigned above
EXECUTE sp_executesql N'SELECT @@SERVERNAME ServerName,DB_NAME() DatabaseName,o.type_desc ObjectType,s.name SchemaName,o.name ObjectName,c.name ColumnName,t.name DataType,c.column_id,c.is_identity,c.max_length,c.precision,c.scale,c.is_nullable,ep.value ExtendedProperty FROM sys.all_objects o LEFT OUTER JOIN sys.all_columns c ON o.object_id = c.object_id LEFT OUTER JOIN sys.types t	ON c.user_type_id = t.user_type_id LEFT OUTER JOIN sys.schemas s ON o.schema_id = s.schema_id LEFT OUTER JOIN sys.extended_properties ep ON ep.major_id = o.object_id and ep.minor_id = c.column_id WHERE c.name LIKE ''%''+@search+''%'' OR o.name LIKE ''%''+@search+''%'' OR s.name LIKE ''%''+@search+''%'' OR t.name LIKE ''%''+@search+''%'' ORDER BY o.name,s.name,c.name',N'@search nvarchar(200)',@search =